import './App.css';
import React, {useState} from "react";

function App() {

  const [todoList, settodoList] = useState([]);
  const [todoTitle, settodoTitle] = useState("");

  const addTask = (task) => {
    const newTask = {
      id:Math.random(),
      task: task,
    };

    settodoList([...todoList, newTask]);
    settodoTitle("");
  }

  return (
    <section>
      <div className="title">
        <h1>To-Do List</h1>
      </div>

      <div className="addTask">
        <input type="text" value={input} onChange={(e)=>settodoTitle(e.target.value)} className="inputTask" placeholder="Add a Task..."></input>
        <button className="addBtn" onClick={()=>addTask(input)}><b>Add</b></button>
      </div>

      {todoList.map((task) => (

        <div className="taskBox">
          <div className="todo-container">
            <h2>To-Do Tasks: </h2>
          </div>

          <div className="tasks-container">
            <div className="tasks">
              <h2>Task 1</h2>

              <div className="tasksBtn">
                <button className="updateContainer">
                  <img className="updateBtn" src={require("./update.png")} height={20} width={20} />
                </button>
                <button className="deleteContainer">
                  <img className="deleteBtn" src={require("./delete.png")} heigh={20} width={20} />
                </button>
              </div>
            </div>
          </div>
        </div>

      ))}

    </section>

  );
}

export default App;
