import './App.css';
import React, {useState} from "react";

function App() {

  const [todoList, settodoList] = useState([]);
  const [todoTitle, settodoTitle] = useState("");

  const addTask = () => {
    let newTask = {
      title:todoTitle,
    }

    let todoArray = [...todoList];
    todoArray.push(newTask);

    settodoList(todoArray);
  }

  /*const updateTask = (index) => {
    let editTask = todoList.find((index)=> {
      return 
    })
  }*/

  const updateTask = (index) => {
    setTodoTitle(todoList[index].title);
    setEditIndex(index);
  }

  return (
    <section>
      <div className="title">
        <h1>To-Do List</h1>
      </div>

      <div className="addTask">
        <input type="text" value={todoTitle} onChange={(e)=>settodoTitle(e.target.value)} className="inputTask" placeholder="Add a Task..."></input>
        <button className="addBtn" onClick={addTask}><b>Add</b></button>
      </div>

      <div className="taskBox">
          <div className="todo-container">
            <h2>To-Do Tasks: </h2>
          </div>

          <div className="tasks-container">

            {todoList.map((item, index) => {
              return(
                <div className="tasks"key={index}>
                  <h2>{item.title}</h2>

                  <div className="tasksBtn">
                    <button className="updateContainer" onClick={()=>updateTask(index)}>
                      <img className="updateBtn" src={require("./update.png")} height={20} width={20} />
                    </button>
                    <button className="deleteContainer">
                      <img className="deleteBtn" src={require("./delete.png")} heigh={20} width={20} />
                    </button>
                  </div>
                </div>
              )  
            })}

          </div>
      </div>
    </section>

  );
}

export default App;
