  };


  return (
    <section>

      <div className="title">
        <h1>To-Do List</h1>
      </div>

      <form className="addTask" onSubmit={handleSubmit}>
        <input 
          type="text"
          value={input}
          name='text'
          className="inputTask" 
          placeholder="Add a Task..."
        />

        <button 
          className="addBtn">
          <b>Add</b>
        </button>
      </form>

      <div className="taskBox">
        <div className="todo-container">
          <h2>To-Do Tasks: </h2>
        </div>

        <div className="tasks-container">
          <div className="tasks">
            <h2>Task 1</h2>
            
            <div className="tasksBtn">
              <button className="updateContainer">
                <img className="updateBtn" src={require("../update.png")} height={20} width={20} />
              </button>
              <button className="deleteContainer">
                <img className="deleteBtn" src={require("../delete.png")} heigh={20} width={20} />
              </button>
            </div>
          </div>
        </div>
      </div>

    </section>
  );
}

export default App;
